import React from 'react';
import ReactDOM from 'react-dom';
import calculateDistance from './util'
import axios from 'axios';


const home = "De Bilt";
var cityFound = true;

class InformationSelector extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            currentLocation: home,
            homeLat: "",
            homeLng: "",
            city: "De Bilt",
            isTemperature: false,
            isDistance: false,
            lat: "",
            lng: ""
        };
    }
    onChangeTemperature = () => {
        this.setState(initialState => ({
            isTemperature: !initialState.isTemperature,
        }));
    }
    onChangeDistance = () => {
        this.setState(initialState => ({
            isDistance: !initialState.isDistance,
        }));
    }
    onChangeCity = (e) => {
        const citySelected = e.target.value;
        this.setState({city: citySelected});
    }

    getCurrentLocation() {
        axios.post('http://localhost:3000/location/getCurrentLocation', {
            cityName: this.state.currentLocation
        })
        .then((res) => {
            this.setState({country: "",
                homeLat: res.data.geonames[0].lat,
                homeLng: res.data.geonames[0].lng
            });
        })
    }

    getCityInfo() {
        axios.post('http://localhost:3000/location/getCityInfo', {
            cityName: this.state.city
        })
        .then((res) => {
            if(res.data.geonames.length>0){
                cityFound = true;
                this.setState({
                    country: res.data.geonames[0].countryName,
                    lat: res.data.geonames[0].lat, lng: res.data.geonames[0].lng
                })
                ReactDOM.render(<p>Country: {this.state.country}</p>, document.getElementById('countryName'))
            }
            else {
                cityFound = false;
                ReactDOM.render(<p>City not found</p>, document.getElementById('cityName'));
                ReactDOM.render("", document.getElementById('countryName'));
                ReactDOM.render("", document.getElementById('temperature'));
                ReactDOM.render("", document.getElementById('distance'));
            }
        })
    }

    getTemperature = () => {
        axios.post('http://localhost:3000/temperature/getTemperature', {
            cityName: this.state.city
        })
        .then((res) => {
            console.log(res) 
            this.setState({temperature: res.data.toFixed(0) });
        })
        return <div>Temperature: {this.state.temperature} degrees Celcius</div>;
    }

    getCityInfoTypes = () => {
        if (this.state.isTemperature){
            ReactDOM.render(< this.getTemperature />, document.getElementById('temperature'));
        }
        else{
            ReactDOM.render("", document.getElementById('temperature'));
        }
        if (this.state.isDistance){
            ReactDOM.render(<p>Distance from {this.state.currentLocation} to {this.state.city}:<span>&nbsp;</span>
             {calculateDistance(this.state.homeLat, this.state.homeLng,
                                    this.state.lat, this.state.lng)} km</p>, document.getElementById('distance'));
        }
        else {
            ReactDOM.render("", document.getElementById('distance'));
        }
    }

    onSubmit = (e) => {
        e.preventDefault();
        this.getCurrentLocation();
        this.getCityInfo();
        if(cityFound === true) {
            this.getCityInfoTypes();
            ReactDOM.render(<p>City: {this.state.city}</p>, document.getElementById('cityName'));
        }
        console.log(this.state);
    }
    render() {
        return (
        <div id="formCitySearch">
         <form onSubmit={this.onSubmit}>
              <h1>Choose a city and the information you wish to see</h1>
              <input type='text' onChange={this.onChangeCity} />
                <p>
                <label>
                  Temperature
                  <input
                    name="Temperature"
                    type="checkbox"
                    checked={this.state.name}
                    onChange={this.onChangeTemperature}/>
                </label>
                <label>
                  Distance
                  <input
                    name="Distance"
                    type="checkbox"
                    checked={this.state.name}
                    onChange={this.onChangeDistance}/>
                </label>
              </p>
              <button>
                Get Info
              </button>

          </form>
          <div id="wrapper">
              <div id="searchInfo"></div>
              <div id="SearchByCity">
                <div id="currentCity"></div>
                <div id="selectInformationTypes"></div>
                <div id="cityName"></div>
                <div id="countryName"></div>
                <div id="latlng"></div>
                <div id="temperature"></div>
                <div id="distance"></div>
                <div id="temperatureInfo"></div>
              </div>
          </div>
       </div>
       );
    }
}

export default InformationSelector
