import React from 'react'

class FetchTemperature extends React.Component {
    getTemperature = (props) => {
        const url = "http://api.openweathermap.org/data/2.5/weather?q=" + props.city + "&APPID=840e44ccd3376f040093e83e5be8f34f";
        console.log(url);
        fetch(url)
        .then(res => res.json())
        .then(data => {
            console.log("data: ");
            console.log(data);
            console.log(data.main.temp);
            const temp = Number(data.main.temp) - 273.15;
            this.setState({temperature: temp.toFixed(0) });
        })
        .catch(console.log);
    }

    render() {
    return(
        <div>Temperature: {this.state.temperature} degrees Celcius</div>
    )}
}

export default FetchTemperature
