import React from 'react';
import ReactDOM from 'react-dom';
import axios from 'axios';
import Navbar from './components/navbar.component'
import './index.css'
import textLogo from './images/DFTextTransparant.png'
import calculateDistance from './util'
import CapitalInit from './CapitalInit'

const home = "De Bilt";
var homeLat = 52.11;
var homeLng = 5.18;

ReactDOM.render(< Navbar />, document.getElementById('navbar'));
ReactDOM.render(<img src={textLogo} alt="nameLogo" />, document.getElementById('nameLogo'));


class InformationSelector extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            currentLocation: home,
            homeLat: "",
            homeLng: "",
            city: "De Bilt",
            isTemperature: false,
            isDistance: false,
            lat: "",
            lng: ""
        };
    }
    onChangeTemperature = () => {
        this.setState(initialState => ({
            isTemperature: !initialState.isTemperature,
        }));
    }
    onChangeDistance = () => {
        this.setState(initialState => ({
            isDistance: !initialState.isDistance,
        }));
    }
    onChangeCity = (e) => {
        const citySelected = e.target.value;
        this.setState({city: citySelected});
    }

    componentDidMountCurrentLocation(url) {
        fetch(url)
        .then(res => res.json())
        .then(cityData => this.setState({country: "",
            homeLat: cityData.geonames[0].lat, homeLng: cityData.geonames[0].lng}, () =>
            ReactDOM.render(<p>Current city: {this.state.currentLocation}</p>, document.getElementById('currentCity')),
        ));
        }
    componentDidMountCity(url) {
        fetch(url)
        .then(res => res.json())
        .then(cityData => this.setState({country: cityData.geonames[0].countryName,
            lat: cityData.geonames[0].lat, lng: cityData.geonames[0].lng}, () =>
            ReactDOM.render(<p>Country: {this.state.country}</p>, document.getElementById('countryName')),
            ReactDOM.render(<p>Lat: {this.state.lat}, Lng: {this.state.lng}</p>, document.getElementById('latlng'))
        ));
    }

    getTemperature = () => {
        console.log("lat and lng in function: " + this.state.lat + " + " + this.state.lng);
        const url = "http://api.geonames.org/findNearByWeatherJSON?lat=" + this.state.lat + "&lng=" + this.state.lng +  "&username=destinationFinder";
        console.log(url);
        fetch(url)
        .then(res => res.json())
        .then(data => {
            console.log("data: ");
            console.log(data);
            this.setState({temperature: data.weatherObservation.temperature, });
        })
        .catch(console.log);

        return <div>Temperature: {this.state.temperature} degrees</div>;
    }

    onSubmit = (e) => {
        e.preventDefault();
        var username = "destinationFinder"
        this.setState({userName: username});
        var url = "http://api.geonames.org/searchJSON?name_equals=" + this.state.currentLocation + "&username=" + username;
        this.componentDidMountCurrentLocation(url);

        url = "http://api.geonames.org/searchJSON?name_equals=" + this.state.city + "&username=" + username;
        this.componentDidMountCity(url);

        console.log("lat: " + this.state.lat);
        console.log("homelat: " + this.state.homeLat);
        ReactDOM.render(<p>{this.state.city}</p>, document.getElementById('cityName'));
        if (this.state.isTemperature){
            console.log("lat and lng: " + this.state.lat + this.state.lng);
            ReactDOM.render(< this.getTemperature />, document.getElementById('temperatureInfo'));
        }
        else{
            ReactDOM.render("", document.getElementById('temperature'));
        }
        if (this.state.isDistance){
            ReactDOM.render(<p>Distance from {this.state.currentLocation} to {this.state.city}:<span>&nbsp;</span>
             {calculateDistance(this.state.homeLat, this.state.homeLng,
                                    this.state.lat, this.state.lng)} km</p>, document.getElementById('distance'));
        }
        else {
            ReactDOM.render("", document.getElementById('distance'));
        }
        //ReactDOM.render(<FetchCity oldState={this.state} />, document.getElementById('temperatureInfo'));
    }
    render() {
        return (
         <form onSubmit={this.onSubmit}>
              <input type='text' onChange={this.onChangeCity} />
                <p>
                <label>
                  Temperature
                  <input
                    name="Temperature"
                    type="checkbox"
                    checked={this.state.name}
                    onChange={this.onChangeTemperature}/>
                </label>
                <label>
                  Distance
                  <input
                    name="Distance"
                    type="checkbox"
                    checked={this.state.name}
                    onChange={this.onChangeDistance}/>
                </label>
              </p>
              <button>
                Get Info
              </button>
          </form>
        );


    }
}

//ReactDOM.render(<InformationSelector />, document.getElementById('selectInformationTypes'));


ReactDOM.render(<CapitalInit />, document.getElementById('fillDBWithCapitals'));

class ShowCapitals extends React.Component {

    SortCapitalsDistance = (props) => {
        props.msg.sort(function (a, b) {
            return a.distance - b.distance;
        })

        let answer = [];
        answer[0] = "City, Distance";
        let i;
        for (i = 1; i<props.msg.length;i++){
            answer[i] = <p> {props.msg[i].cityName}, {props.msg[i].distance}  </p>
        }

        return (
            answer
        )
    }

    PrintCapitalsDistance = (props) => {
        let answer = [];
        answer[0] = "City, Distance";
        let i;
        for (i = 1; i<5;i++){
            answer[i] = <p> {props.msg[i].cityName}, {props.msg[i].distance}  </p>
        }

        return (
            answer
        )
    }

    showCapitals = (async () => {
        const response = await axios.get('http://localhost:5000/cities')
        console.log(response.data)
        //ReactDOM.render(<this.PrintCapitalsDistance msg={response.data} />, document.getElementById('showDBInfo'));

        ReactDOM.render(<this.SortCapitalsDistance msg={response.data} />, document.getElementById('showDBInfoSorted'));
    })

    render() {
        return(
            <div>
                <button onClick={this.showCapitals} >Show Capitals</button>
            </div>
        )
    }
}
//ReactDOM.render(<ShowCapitals />, document.getElementById('showDB'));
