import React, { Component } from 'react';
import axios from 'axios';



class AddComment extends Component {

    constructor(){
        super();
        this.state = {
            cities: "",
            ratingMessage: "",
            selectedRating: "option3"
        }
    }

    componentDidMount = () => {
        axios.get('http://localhost:5000/cities')
        .then(response => {
            this.setState({cities: response.data})
            this.initializeCities()
        })
    }

    initializeCities(){
        // get cities from DB
        var cityInfo = this.state.cities;
        var cities = [];
        for (let i = 0; i<cityInfo.length;i++){
            cities[i] = cityInfo[i].cityName;
        }
        cities.sort();
        var select = document.getElementById('ratingSelectBtn');
        for(let x = 0; x<cities.length; x++){
            select.options[select.options.length] = new Option(cities[x], cities[x]);
        }

    }

    handleChange(e){
        this.setState({ratingMessage: e.target.value})
    }

    handleRatingChange(e){
        this.setState({selectedRating: e.target.value})
    }


    handleSubmit(e){
        let city = e.target[0].value;
        let message = e.target[1].value;
        console.log(city + ": " + message);
    }


    render(){
        return(
            <div id="commentWrapper">
                <form onSubmit={this.handleSubmit}>>
                    <h1 id="chooseCityTxt">Rate a city</h1>
                    <label> City
                        <select id="ratingSelectBtn" onChange={this.onChangeSort}>
                            <option defaultValue="start">Select a city</option>
                        </select>
                    </label>
                    <label> Rating
                        <input type="radio" value="option1" checked={this.state.selectedRating === "option1"}
                                                            onChange={this.handleRatingChange} />
                        1
                        <input type="radio" value="option2" checked={this.state.selectedRating === "option2"}
                                                            onChange={this.handleRatingChange} />
                        2
                        <input type="radio" value="option3" checked={this.state.selectedRating === "option3"}
                                                            onChange={this.handleRatingChange} />
                        3
                        <input type="radio" value="option4" checked={this.state.selectedRating === "option4"}
                                                            onChange={this.handleRatingChange} />
                        4
                        <input type="radio" value="option5" checked={this.state.selectedRating === "option5"}
                                                            onChange={this.handleRatingChange} />
                        5
                    </label>
                    <label> Message
                         <textarea defaultValue={this.state.ratingMessage} onChange={this.onChange} />
                    </label>
                    <input type="submit" value="Submit" />
                </form>
            </div>
        )
    }
}

export default AddComment
