import React from 'react'
import calculateDistance from './util'
import axios from 'axios';

var homeLat = 52.11;
var homeLng = 5.18;

class CapitalInit extends React.Component {
    initDB = () => {
        const url = "http://api.geonames.org/countryInfoJSON?formatted=true&username=destinationFinder";
        fetch(url)
        .then(res => res.json())
        .then(data => {
            let i;
            for (i = 0; i< data.geonames.length;i++){
                if(data.geonames[i].capital.length > 0){
                    const name = data.geonames[i].capital;
                    const countryName = data.geonames[i].countryName;
                    const spokenLanguages = data.geonames[i].languages;
                    let urlCitySpecific = "http://api.geonames.org/searchJSON?q=" + name + "&maxRows=2&username=destinationFinder";
                    axios.post('http://localhost:3000/location/getWeather', {
                        capitalName: name
                    })
                    fetch(urlCitySpecific)
                    .then(res => res.json())
                    .then(data => {
                        let lat = data.geonames[0].lat;
                        let lng = data.geonames[0].lng;
                        const distance = calculateDistance(homeLat, homeLng, lat, lng);


                        axios.post('http://localhost:3000/weather/getWeather', {
                            capitalName: name
                        })
                        .then((res) => {
                            try{
                                let temp = res.data.main.temp;
                                let tempCelcius = (temp - 273.15).toFixed(0);
                                const city = {
                                    "cityName": name,
                                    "countryName": countryName,
                                    "spokenLanguages": spokenLanguages,
                                    "distance": distance,
                                    "temperature": tempCelcius,
                                    "cityLat": lat,
                                    "cityLng": lng,
                                    "currentRating": 3,
                                }
                                axios.post('http://10.10.4.4:5000/cities/add', city)
                            }
                            catch(e){
                                console.log(e);
                            }
                        })
                    })
                }
            }
            console.log("DB filled with capitals");
        })
        .catch(console.log);
    }

    render() {
    return(
        <div id="DBInitBtn">
            <button onClick={this.initDB} >Initialize Database</button>
        </div>
    )}
}

export default CapitalInit
