        this.setState({city:city});
    }

    getData(){
        var urlStart = "api.geonames.org/search?name_equals=";
        var user = "&user=DestinationFinder";
        var url = urlStart + InformationSelector.state.city + user;
        return url;
    }

    render(){
        return <div>
