export default class Register extends Component {
    constructor(props) {
        super(props)
        this.state = {
            email : '',
            password: '',
            passwordAgain: ''
        };
    }

    handleInputChange = (event) => {
        const { value, name } = event.target;
        this.setState({
            [name]: value
        });
    }

    onSubmit = (event) => {
        event.preventDefault();
        if(this.state.password !== this.state.passwordAgain) {
            alert("Passwords don't match!");
