import React from 'react';
import ReactDOM from 'react-dom';


class InformationSelector extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            currentLocation: "De Bilt",
            homeLat: "",
            homeLng: "",
            city: "De Bilt",
            isTemperature: false,
            isDistance: false
        };
    }
    onChangeTemperature = () => {
        this.setState(initialState => ({
            isTemperature: !initialState.isTemperature,
        }));
    }
    onChangeDistance = () => {
        this.setState(initialState => ({
            isDistance: !initialState.isDistance,
        }));
    }

    onChangeCity = (e) => {
        const citySelected = e.target.value;
        this.setState({city: citySelected});
    }

    componentDidMountCurrentLocation(url) {
            fetch(url)
            .then(res => res.json())
            .then(cityData => this.setState({country: "",
                homeLat: cityData.geonames[0].lat, homeLng: cityData.geonames[0].lng}, () =>
                ReactDOM.render(<p>Current city: {this.state.currentLocation}</p>, document.getElementById('currentCity')),
            ));
        }

    componentDidMountCity(url) {
        fetch(url)
        .then(res => res.json())
        .then(cityData => this.setState({country: cityData.geonames[0].countryName,
            lat: cityData.geonames[0].lat, lng: cityData.geonames[0].lng}, () =>
            ReactDOM.render(<p>Country: {this.state.country}</p>, document.getElementById('countryName')),
            ReactDOM.render(<p>Lat: {this.state.lat}, Lng: {this.state.lng}</p>, document.getElementById('latlng'))
        ));
    }

    calculateDistance(lat1, lon1, lat2, lon2){
        let radius = 6371;
        let dLat = this.deg2rad(lat2-lat1);  // deg2rad below
        let dLon = this.deg2rad(lon2-lon1);
        let a = Math.sin(dLat/2) * Math.sin(dLat/2) +
            Math.cos(this.deg2rad(lat1)) * Math.cos(this.deg2rad(lat2)) *
            Math.sin(dLon/2) * Math.sin(dLon/2);
        let c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
        let distance = radius * c;
        return distance;
    }

   deg2rad(deg) {
      return deg * (Math.PI/180)
    }

    onSubmit = (e) => {

        e.preventDefault();
        var username = "destinationFinder"
        var url = "http://api.geonames.org/searchJSON?name_equals=" + this.state.currentLocation + "&username=" + username;
        this.componentDidMountCurrentLocation(url);

        url = "http://api.geonames.org/searchJSON?name_equals=" + this.state.city + "&username=" + username;
        this.componentDidMountCity(url);

        console.log("lat: " + this.state.lat);
        console.log("homelat: " + this.state.homeLat);
        ReactDOM.render(<p>{this.state.city}</p>, document.getElementById('cityName'));
        if (this.state.isTemperature){
            ReactDOM.render(<p>Asked for temperature</p>, document.getElementById('temperature'));
        }
        else{
            ReactDOM.render("", document.getElementById('temperature'));
        }
        if (this.state.isDistance){
            ReactDOM.render(<p>Distance from {this.state.currentLocation} to {this.state.city}:
            {this.calculateDistance(this.state.homeLat, this.state.homeLng,
                                    this.state.lat, this.state.lng)}</p>, document.getElementById('distance'));
        }
        else {
            ReactDOM.render("", document.getElementById('distance'));
        }
        //ReactDOM.render(<FetchCity oldState={this.state} />, document.getElementById('temperatureInfo'));
    }
    render() {
        return (
         <form onSubmit={this.onSubmit}>
              <select onChange={this.onChangeCity}>
                <option value="De Bilt">De Bilt</option>
                <option value="Manchester">Manchester</option>
              </select>
              <p>
                <label>
                  Temperature
                  <input
                    name="Temperature"
                    type="checkbox"
                    checked={this.state.name}
                    onChange={this.onChangeTemperature}/>
                </label>
                <label>
                  Distance
                  <input
                    name="Distance"
                    type="checkbox"
                    checked={this.state.name}
                    onChange={this.onChangeDistance}/>
                </label>
              </p>
              <button>
                Get Info
              </button>
          </form>
        );
    }
}

ReactDOM.render(<InformationSelector />, document.getElementById('selectInformationTypes'));

class FetchTemperature extends InformationSelector{
    constructor(props) {
        super(props);
        this.state = {
            url: "http://api.geonames.org/searchJSON?name_equals=" + props.oldState.city + "&username=DestinationFinder",
            lat: "",
            lng: "",
        };
//    console.log(this.state);
//    console.log(props.oldState);
    }

    async componentDidMount(){
        const url = this.state.url;
        const response = await fetch(url);
        const data = await response.json();
        this.setState({lat: data.geonames[1].lat, lng: data.geonames[1].lng, country: data.geonames[1].countryName})
//        console.log(data.geonames[0]);
//        console.log(this.state);
    }

    render(){
        return <div>
            {<div>{this.state.city}
                country: {this.state.country},
                lat: {this.state.lat},
                lng: {this.state.lng}
            </div>}

        </div>;
    }
}
