import React, { Component } from 'react';
import axios from 'axios';



class AddComment extends Component {

    constructor(){
        super();
        this.state = {
            cities: "",
            ratingMessage: ""
        }
    }

    componentDidMount = () => {
        const response = axios.get('http://localhost:5000/cities')
        .then(response => {
            this.setState({cities: response.data})
            this.initializeCities()
        })
    }

    initializeCities(){
        // get cities from DB
        var cityInfo = this.state.cities;
        var cities = [];
        for (let i = 0; i<cityInfo.length;i++){
            cities[i] = cityInfo[i].cityName;
        }
        cities.sort();
        var select = document.getElementById('ratingSelectBtn');
        for(let x = 0; x<cities.length; x++){
            select.options[select.options.length] = new Option(cities[x], cities[x]);
        }

    }

    handleChange(e){
        this.setState({ratingMessage: e.target.value})
    }

    render(){
        return(
            <div id="commentWrapper">
                <form>
                    <h1 id="chooseCityTxt">Choose a city</h1>

                    <select id="ratingSelectBtn" onChange={this.onChangeSort}>
                        <option defaultValue="start">Select a city</option>
                    </select>
                    <textarea defaultValue={this.state.ratingMessage} onChange={this.onChange} />
                </form>
            </div>
        )
    }
}

export default AddComment
