import React, { Component } from 'react';
import axios from 'axios';



class AddComment extends Component {


    componentDidMount = () => {
        const response = axios.get('http://localhost:5000/cities')
        .then(response => response.json())
        .then(cityData => console.log(cityData))
    }

    initializeCities(){
        // get cities from DB

        let testList = ["a", "b", "c"];
        let testList2 = [1,2,3];
        let testList3 = [{
            "a": 1,
            "b": 2
        },{
            "a": 5,
            "b": 2
        }]
        var select = document.getElementById('ratingSelectBtn');
        for(let x = 0; x<3; x++){
            select.options[select.options.length] = new Option(testList[x], testList2[x]);
        }
    }

    render(){
        return(
            <div id="commentWrapper">
                <h1 id="chooseCityTxt">Choose a city</h1>
                <button id="ratingInitBtn" onClick={this.initializeCities}>
                    Initialize
                </button>

                <select id="ratingSelectBtn" onChange={this.onChangeSort}>
                    <option value="start">Select a city</option>
                </select>
            </div>
        )
    }
}

export default AddComment
