import React from 'react';
import InformationSelector from './index.js';

class FetchCity extends React.Component{

    state = {
        loading: true,
        city: "",
        url: ""
    };

    setCity(city){
        this.setState({city:city});
    }

    getData(){
        var urlStart = "api.geonames.org/search?name_equals=";
        var user = "&user=DestinationFinder";
        this.setState({url: urlStart + InformationSelector.state.city + user});
    }

    render(){
        {this.getData()}
        return <div>
            {this.state.loading ? <div>{this.state.url}</div> : <div>cityName</div>}
        </div>;
    }
}

export default FetchCity;
