    render() {
        return (
        <div id="errorMSG">
          <form onSubmit={this.onSubmit}>
            <h1>Login</h1>
            <input
              type="email"
              name="email"
              placeholder="Enter email"
              value={this.state.email}
              onChange={this.onChange}
              required
            />
            <input
              type="password"
              name="password"
              placeholder="Enter password"
              value={this.state.password}
              onChange={this.onChange}
              required
            />
           <input type="submit" value="Submit"/>
          </form>
        </div>
        );
    }
}
