import React from 'react';
import ReactDOM from 'react-dom';


class InformationSelector extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            city: "De Bilt",
            isTemperature: false,
            isDistance: false
        };
    }
    onChangeTemperature = () => {
        this.setState(initialState => ({
            isTemperature: !initialState.isTemperature,
        }));
    }
    onChangeDistance = () => {
        this.setState(initialState => ({
            isDistance: !initialState.isDistance,
        }));
    }

    onChangeCity = (e) => {
        const citySelected = e.target.value;
        this.setState({city: citySelected});
    }

    componentDidMount(url) {
        fetch(url)
            .then(res => res.json())
            .then(cityData => this.setState({country: cityData.geonames[0].countryName,
                lat: cityData.geonames[0].lat, lng: cityData.geonames[0].lng}, () =>
                ReactDOM.render(<p>Country: {this.state.country}</p>, document.getElementById('countryName'))));
    }

    onSubmit = (e) => {
        e.preventDefault();

        var url = "http://api.geonames.org/searchJSON?name_equals=" + this.state.city + "&username=DestinationFinder";
        this.componentDidMount(url);


        ReactDOM.render(<p>{this.state.city}</p>, document.getElementById('cityName'));
        if (this.state.isTemperature){
            ReactDOM.render(<p>Asked for temperature</p>, document.getElementById('temperature'));
        }
        else{
            ReactDOM.render("", document.getElementById('temperature'));
        }
        if (this.state.isDistance){
            ReactDOM.render(<p>Asked for distance</p>, document.getElementById('distance'));
        }
        else {
            ReactDOM.render("", document.getElementById('distance'));
        }
        //ReactDOM.render(<FetchCity oldState={this.state} />, document.getElementById('temperatureInfo'));
    }
    render() {
        return (
         <form onSubmit={this.onSubmit}>
              <select onChange={this.onChangeCity}>
                <option value="De Bilt">De Bilt</option>
                <option value="Manchester">Manchester</option>
              </select>
              <p>
                <label>
                  Temperature
                  <input
                    name="Temperature"
                    type="checkbox"
                    checked={this.state.name}
                    onChange={this.onChangeTemperature}/>
                </label>
                <label>
                  Distance
                  <input
                    name="Distance"
                    type="checkbox"
                    checked={this.state.name}
                    onChange={this.onChangeDistance}/>
                </label>
              </p>
              <button>
                Get Info
              </button>
          </form>
        );
    }
}

ReactDOM.render(<InformationSelector />, document.getElementById('selectInformationTypes'));



