const Validator = require("validator");


export default class Register extends Component {
    constructor(props) {
        super(props)
        this.state = {
            email : '',
            password: '',
            passwordAgain: ''
        };
    }

    handleInputChange = (event) => {
        const { value, name } = event.target;
        this.setState({
            [name]: value
        });
    }

    onSubmit = (event) => {
